import { configure } from 'enzyme';
import Adapter from 'enzyme-adapter-react-16';

configure({ adapter: new Adapter() });

import { JSDOM } from 'jsdom';
import { expect } from 'chai';
import React from 'react';
import { mount } from 'enzyme';
import Cabinet from '../components/User/Cabinet';
import fetchMock from 'fetch-mock';
import sinon from 'sinon';

const dom = new JSDOM('<!doctype html><html><body></body></html>');
global.window = dom.window;
global.document = dom.window.document;
global.localStorage = {
  getItem: sinon.stub(),
  setItem: sinon.stub(),
  removeItem: sinon.stub(),
  clear: sinon.stub(),
};

describe('<Cabinet />', () => {
  let wrapper;
  const fakeUser = {
    login: 'testUser',
    full_name: 'Test User',
    passport_number: '123456',
    card_number: '1234567890',
    date_of_birth: '1990-01-01',
    credit_history: 'Good',
    email: 'test@example.com',
    phone_number: '+123456789',
  };

  beforeEach(() => {
    global.localStorage.getItem.returns('1');
    fetchMock.get(`http://127.0.0.1:5000/user/1`, {
      body: fakeUser,
      status: 200,
    });
    wrapper = mount(<Cabinet />);
  });

  afterEach(() => {
    fetchMock.reset();
    sinon.resetHistory();
  });

  it('renders user data correctly', () => {
    return new Promise((resolve) => {
      setImmediate(() => {
        wrapper.update();
        expect(wrapper.find('p').at(0).text()).to.equal(`Login: ${fakeUser.login}`);
        expect(wrapper.find('p').at(1).text()).to.equal(`Full Name: ${fakeUser.full_name}`);
        expect(wrapper.find('p').at(2).text()).to.equal(`Passport Number: ${fakeUser.passport_number}`);
        expect(wrapper.find('p').at(3).text()).to.equal(`Card Number: ${fakeUser.card_number}`);
        expect(wrapper.find('p').at(4).text()).to.equal(`Date Of Birth: ${fakeUser.date_of_birth}`);
        expect(wrapper.find('p').at(5).text()).to.equal(`Credit History: ${fakeUser.credit_history}`);
        expect(wrapper.find('p').at(6).text()).to.equal(`Email: ${fakeUser.email}`);
        expect(wrapper.find('p').at(7).text()).to.equal(`Phone Number: ${fakeUser.phone_number}`);
        resolve();
      });
    });
  });

});

import React, { useEffect, useState } from 'react';

const Cabinet = () => {
  const [userInfo, setUserInfo] = useState({
    login: 'No data available',
    full_name: 'No data available',
    passport_number: 'No data available',
    card_number: 'No data available',
    date_of_birth: 'No data available',
    credit_history: 'No data available',
    email: 'No data available',
    phone_number: 'No data available',
  });

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    const token = localStorage.getItem('jwtToken');

    fetch(`http://127.0.0.1:5000/user/${userId}`, {
      method: 'GET',
      headers: {
        'Authorization': 'Bearer ' + token
      }
    })
    .then(response => {                                     // istanbul ignore next
      if (!response.ok) {                                    
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      setUserInfo(prevState => ({
        ...prevState,
        ...data
      }));
    })

    .catch((error) => {                                      // istanbul ignore next
      console.error('Error:', error);                        // istanbul ignore next
      window.location.href = "/login";                       // istanbul ignore next
      localStorage.removeItem('jwtToken');                   // istanbul ignore next
      localStorage.removeItem('userId');                     // istanbul ignore next
      window.location.href = '/login';                       // istanbul ignore next
      alert('Connection timed out. Please log in again');    
    });

  }, []);

  const styles = {
    body: {
      fontFamily: "'Open Sans', sans-serif",
      textAlign: 'center',
      color: '#F8F8F2'
    },
    container: {
      width: 500,
      margin: '5px auto 5px auto',
      border: '1px solid #FF6347',
      borderRadius: 5,
      backgroundColor: '#23272A',
      position: 'relative'
    },
    h1: {
      color: '#FF6347',
      padding: 20,
      fontFamily: "'Merriweather', serif",
      fontSize: '2.5em',
    },
    h2: {
      color: '#FF6347',
      fontFamily: "'Merriweather', serif",
    },
    p: {
      color: '#F8F8F2',
      fontSize: '1.2em'
    },
    infoBlock: {
      margin: 20,
      padding: 10,
      backgroundColor: '#2C2F33',
      borderRadius: 5
    },
    contactInfo: {
      margin: 20,
      padding: 10,
      color: '#F8F8F2',
      fontSize: '1.0em'
    },
    backButton: {
      position: 'absolute',
      top: 10,
      fontSize: '2em',
      color: '#FF6347',
      textDecoration: 'none',
    }
  };

  return (
    <div style={styles.body}>
      <div style={styles.container}>
        <h1 style={styles.h1}>Micro Coin Cabinet</h1>
      </div>
      <div style={styles.container}>
        <div style={styles.infoBlock}>
          <h2 style={styles.h2}>User Information</h2>
          <p style={styles.p}><strong style={{color: '#0084ff'}}>Login:</strong> {userInfo.login}</p>
          <p style={styles.p}><strong style={{color: '#0084ff'}}>Full Name:</strong> {userInfo.full_name}</p>
          <p style={styles.p}><strong style={{color: '#0084ff'}}>Passport Number:</strong> {userInfo.passport_number}</p>
          <p style={styles.p}><strong style={{color: '#0084ff'}}>Card Number:</strong> {userInfo.card_number}</p>
        </div>
        <div style={styles.infoBlock}>
          <h2 style={styles.h2}>About User</h2>
          <p style={styles.p}><strong style={{color: '#0084ff'}}>Date Of Birth:</strong> {userInfo.date_of_birth}</p>
          <p style={styles.p}><strong style={{color: '#0084ff'}}>Credit History:</strong> {userInfo.credit_history}</p>
          <p style={styles.p}><strong style={{color: '#0084ff'}}>Email:</strong> {userInfo.email}</p>
          <p style={styles.p}><strong style={{color: '#0084ff'}}>Phone Number:</strong> {userInfo.phone_number}</p>
        </div>
        <div style={styles.contactInfo}>
          <p>To delete your account or change the account details, please contact us at admin.micro.coin@gmail.com, clearly specifying what you want to change.</p>
          <p>Contact Phone: +380567434856, we are available Mon-Fri from 9:00 to 16:00.</p>
        </div>
      </div>
    </div>
  );
}

export default Cabinet;

import React from 'react';
import { mount } from 'enzyme';
import Faq from '../components/User/Faq';
import { JSDOM } from 'jsdom';
import { expect } from 'chai';


const { window } = new JSDOM('<!doctype html><html><body></body></html>');

global.window = window;
global.document = window.document;
global.navigator = {
  userAgent: 'node.js',
};

describe('Faq', () => {
  it('renders without crashing', () => {
    const wrapper = mount(<Faq />);
    expect(wrapper.exists()).to.be.true;
  });

  it('renders the FAQ questions', () => {
    const wrapper = mount(<Faq />);
    const questions = wrapper.find('h2');
    expect(questions.length).to.equal(21);
  });

  it('toggles the answer visibility when a question is clicked', () => {
    const wrapper = mount(<Faq />);
    const firstQuestion = wrapper.find('h2').first();
    const answer = wrapper.find('p').first();

    expect(answer.prop('style').display).to.equal('none');
    firstQuestion.simulate('click');
    // In case the 'style' object changes after the click you might want to access the updated wrapper
    const updatedAnswerAfterFirstClick = wrapper.find('p').first();
    expect(updatedAnswerAfterFirstClick.prop('style').display).to.equal('block');
    firstQuestion.simulate('click');
    const updatedAnswerAfterSecondClick = wrapper.find('p').first();
    expect(updatedAnswerAfterSecondClick.prop('style').display).to.equal('none');
  });
});

