import React, { useState, useEffect } from 'react';

const style = {
    body: {
        color: 'white',
        fontFamily: "'Roboto', sans-serif",
        textAlign: 'center'
    },
    container: {
        width: '350px',
        margin: '0 auto',
        marginTop: '100px',
